// src/components/Navbar.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const menuItems = [
  { name: 'AAMS', path: '/aams' },
  { name: 'Features', path: '/features' },
  { name: 'Demo', path: '/demo' },
  { name: 'Download', path: '/download' },
  { name: 'Pricing', path: '/pricing' },
  { name: 'Support', path: '/support' },
  { name: 'FAQ', path: '/faq' },
];

const Navbar = ({ isLoggedIn, handleLogin, handleLogout }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const navigate = useNavigate();

  const handleMenuClick = (path) => {
    navigate(path);       // Go to the selected route
    setShowDropdown(false); // Close the dropdown
  };

  return (
    <nav className="bg-blue-600 text-white px-6 py-4 shadow relative">
      <div className="flex justify-between items-center">
        {/* LEFT: AAMS Dropdown */}
        <div className="relative">
          <button
            onClick={() => setShowDropdown(!showDropdown)}
            className="hover:underline font-semibold"
          >
            AAMS â–¾
          </button>

          {showDropdown && (
            <div className="absolute top-10 left-0 bg-white text-black rounded shadow-lg w-72 z-50">
              <ul className="divide-y divide-gray-200">
                {menuItems.map((item) => (
                  <li
                    key={item.name}
                    className="flex justify-between items-center p-3 hover:bg-gray-100"
                  >
                    <span
                      onClick={() => handleMenuClick(item.path)}
                      className="cursor-pointer text-blue-600 hover:underline"
                    >
                      {item.name}
                    </span>
                    {isLoggedIn ? (
                      <button
                        onClick={handleLogout}
                        className="text-sm text-red-500 hover:underline"
                      >
                        Logout
                      </button>
                    ) : (
                      <button
                        onClick={handleLogin}
                        className="text-sm text-green-500 hover:underline"
                      >
                        Login
                      </button>
                    )}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
