import React from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="p-6 text-center">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-blue-500 to-indigo-700 text-white p-10 rounded-xl shadow-lg mb-10 animate-fade-in-up">
        <h1 className="text-5xl font-extrabold mb-4 drop-shadow-lg">
          Welcome to AAMS
        </h1>
        <p className="text-xl text-white/90 max-w-2xl mx-auto">
          Smart Architectural Asset Management — Track, Organize, Optimize.
        </p>
      </div>

      {/* Features Section */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl mx-auto mb-16">
        {[
          {
            title: "Real-time Asset Tracking",
            desc: "Monitor assets across all locations in real time."
          },
          {
            title: "Cloud Management",
            desc: "Access anywhere anytime — on the cloud."
          },
          {
            title: "Security & Backup",
            desc: "Daily backups and industry-standard encryption."
          },
          {
            title: "Analytics & Reports",
            desc: "Make data-driven decisions with detailed insights."
          },
          {
            title: "Multi-user Role Access",
            desc: "Assign roles with different access permissions."
          },
          {
            title: "Mobile Friendly",
            desc: "Manage assets from your smartphone easily."
          }
        ].map((item, index) => (
          <div
            key={index}
            className="p-6 bg-white shadow-xl rounded-xl border hover:scale-[1.02] transition-transform duration-300"
          >
            <h3 className="text-xl font-bold text-blue-600 mb-2">{item.title}</h3>
            <p className="text-gray-600">{item.desc}</p>
          </div>
        ))}
      </div>

      {/* CTA: Login/Signup */}
      <div className="bg-white rounded-xl shadow-2xl p-10 max-w-md mx-auto mt-12">
        <h2 className="text-2xl font-bold text-blue-700 mb-6">Get Started Now</h2>
        <div className="flex flex-col sm:flex-row justify-center items-center gap-4">
          <button
            onClick={() => navigate("/loginlogout")}
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-6 rounded-lg shadow-lg transition"
          >
            Login
          </button>
          <button
            onClick={() => navigate("/loginlogout")}
            className="bg-gray-100 hover:bg-gray-200 text-blue-600 font-semibold py-2 px-6 rounded-lg shadow-md transition"
          >
            Sign Up
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
