import React, { useState } from "react";

const LoginLogout = () => {
  const [registeredUser, setRegisteredUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [formData, setFormData] = useState({ name: "", email: "" });
  const [mode, setMode] = useState("signup");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSignUp = (e) => {
    e.preventDefault();
    if (formData.name.trim() === "" || formData.email.trim() === "") {
      alert("Please enter name and email.");
      return;
    }
    setRegisteredUser({ ...formData });
    setIsLoggedIn(true);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (!registeredUser) {
      alert("No registered user found. Please sign up first.");
      return;
    }
    if (formData.email.trim() === registeredUser.email) {
      setIsLoggedIn(true);
    } else {
      alert("Email not recognized. Please try again or sign up.");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setFormData({ name: "", email: "" });
  };

  return (
    <div className="max-w-md mx-auto p-8 bg-white rounded-xl shadow-2xl transform transition-transform hover:scale-[1.02] duration-300 mt-8">
      {isLoggedIn && registeredUser ? (
        <div className="text-center">
          <h2 className="text-3xl font-extrabold mb-4 text-green-600 drop-shadow-md">
            Welcome, {registeredUser.name}!
          </h2>
          <p className="mb-6 text-gray-600 tracking-wide">{registeredUser.email}</p>
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 shadow-lg text-white px-8 py-3 rounded-lg transition duration-300"
          >
            Logout
          </button>
        </div>
      ) : (
        <>
          <div className="flex justify-center mb-8 border-b-2 border-gray-200">
            <button
              onClick={() => setMode("signup")}
              className={`relative px-6 py-2 font-semibold transition-colors duration-300 ${
                mode === "signup" ? "text-blue-600" : "text-gray-500 hover:text-blue-600"
              }`}
            >
              Sign Up
              {mode === "signup" && (
                <span className="absolute bottom-0 left-0 right-0 h-1 bg-blue-600 rounded-full shadow-lg" />
              )}
            </button>
            <button
              onClick={() => setMode("login")}
              className={`relative px-6 py-2 font-semibold transition-colors duration-300 ${
                mode === "login" ? "text-blue-600" : "text-gray-500 hover:text-blue-600"
              }`}
            >
              Login
              {mode === "login" && (
                <span className="absolute bottom-0 left-0 right-0 h-1 bg-blue-600 rounded-full shadow-lg" />
              )}
            </button>
          </div>

          {mode === "signup" ? (
            <form onSubmit={handleSignUp} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter your name"
                  value={formData.name}
                  onChange={handleChange}
                  className="w-full px-5 py-3 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full px-5 py-3 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300"
                  required
                />
              </div>
              <button
                type="submit"
                className="w-full bg-blue-600 hover:bg-blue-700 shadow-lg text-white py-3 rounded-xl font-semibold transition duration-300"
              >
                Sign Up
              </button>
            </form>
          ) : (
            <form onSubmit={handleLogin} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your registered email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full px-5 py-3 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300"
                  required
                />
              </div>
              <button
                type="submit"
                className="w-full bg-blue-600 hover:bg-blue-700 shadow-lg text-white py-3 rounded-xl font-semibold transition duration-300"
              >
                Login
              </button>
            </form>
          )}
        </>
      )}
    </div>
  );
};

export default LoginLogout;
