import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";

const menuItems = [
  { name: "AAMS", path: "/aams" },
  { name: "Features", path: "/features" },
  { name: "Demo", path: "/demo" },
  { name: "Pricing", path: "/pricing" },
  { name: "Download", path: "/download" },
  { name: "FAQ", path: "/faq" },
  { name: "Support", path: "/support" },
];

const Navbar = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const handleNavigate = (path) => {
    navigate(path);
    setDropdownOpen(false);
  };

  const isHome = location.pathname === "/";

  return (
    <nav className="bg-blue-600 text-white px-6 py-4 shadow relative">
      <div className="flex items-center">
        {isHome ? (
          // Show dropdown when on home
          <div className="relative">
            <button
              onClick={() => setDropdownOpen((prev) => !prev)}
              className="font-semibold hover:underline flex items-center"
            >
              AAMS <span className="ml-1">â–¾</span>
            </button>

            {dropdownOpen && (
              <ul className="absolute mt-2 w-48 bg-white text-black rounded shadow-lg z-50">
                {menuItems.map((item) => (
                  <li
                    key={item.name}
                    className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => handleNavigate(item.path)}
                  >
                    {item.name}
                  </li>
                ))}
              </ul>
            )}
          </div>
        ) : (
          // Show only Home link when not on home
          <button
            onClick={() => navigate("/")}
            className="font-semibold hover:underline"
          >
            Home
          </button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
