import React from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="p-6 text-center">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white p-10 rounded-xl shadow-lg mb-10 animate-fade-in-up">
        <h1 className="text-5xl font-extrabold mb-4 drop-shadow-lg">
          Welcome to AAMS
        </h1>
        <p className="text-xl text-white/90 max-w-2xl mx-auto">
          Architectural Asset Management System designed to optimize, track, and manage your infrastructure with ease.
        </p>
      </div>

      {/* Feature Highlights */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-6xl mx-auto mb-16">
        {[
          {
            title: "Asset Tracking",
            desc: "Real-time location and status tracking of all assets."
          },
          {
            title: "Secure Data",
            desc: "Enterprise-grade encryption ensures your data stays safe."
          },
          {
            title: "Cloud-Based",
            desc: "Access and manage assets from anywhere, anytime."
          }
        ].map((item, index) => (
          <div
            key={index}
            className="p-6 bg-white shadow-xl rounded-xl border hover:scale-[1.02] transition-transform duration-300"
          >
            <h3 className="text-xl font-bold text-blue-600 mb-2">{item.title}</h3>
            <p className="text-gray-600">{item.desc}</p>
          </div>
        ))}
      </div>

      {/* Call to Action */}
      <div className="bg-white rounded-xl shadow-2xl p-10 max-w-md mx-auto">
        <h2 className="text-2xl font-bold text-blue-700 mb-6">Get Started</h2>
        <div className="flex flex-col sm:flex-row justify-center items-center gap-4">
          <button
            onClick={() => navigate("/login")}
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-6 rounded-lg shadow-lg transition"
          >
            Login
          </button>
          <button
            onClick={() => navigate("/signup")}
            className="bg-gray-100 hover:bg-gray-200 text-blue-600 font-semibold py-2 px-6 rounded-lg shadow-md transition"
          >
            Sign Up
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
