// src/components/LoginLogout.jsx
import React, { useState } from 'react';

const LoginLogout = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState({ name: '', email: '' });

  const handleLogin = (e) => {
    e.preventDefault();
    if (user.name && user.email) {
      setIsLoggedIn(true);
    }
  };

  const handleLogout = () => {
    setUser({ name: '', email: '' });
    setIsLoggedIn(false);
  };

  return (
    <div className="mt-10 max-w-md mx-auto p-6 border rounded-lg shadow-lg bg-white">
      {isLoggedIn ? (
        <div className="text-center">
          <h2 className="text-2xl font-semibold mb-4 text-green-600">Welcome, {user.name}!</h2>
          <p className="mb-4 text-gray-600">Email: {user.email}</p>
          <button
            onClick={handleLogout}
            className="bg-red-500 hover:bg-red-600 text-white px-5 py-2 rounded shadow"
          >
            Logout
          </button>
        </div>
      ) : (
        <form onSubmit={handleLogin} className="space-y-6">
          <h2 className="text-2xl font-bold text-blue-600">Login</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Name</label>
            <input
              type="text"
              placeholder="Enter your name"
              value={user.name}
              onChange={(e) => setUser({ ...user, name: e.target.value })}
              className="mt-1 w-full px-4 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-300"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input
              type="email"
              placeholder="Enter your email"
              value={user.email}
              onChange={(e) => setUser({ ...user, email: e.target.value })}
              className="mt-1 w-full px-4 py-2 border rounded-md shadow-sm focus:outline-none focus:ring focus:ring-blue-300"
              required
            />
          </div>
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white w-full py-2 rounded shadow"
          >
            Login
          </button>
        </form>
      )}
    </div>
  );
};

export default LoginLogout;
