import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const menuItems = [
  { name: "AAMS", path: "/aams" },
  { name: "Features", path: "/features" },
  { name: "Demo", path: "/demo" },
  { name: "Pricing", path: "/pricing" },
  { name: "Download", path: "/download" },
  { name: "FAQ", path: "/faq" },
  { name: "Support", path: "/support" },
];

const Navbar = ({ menuVisible, setMenuVisible }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  const handleNavigate = (path) => {
    navigate(path);
    setDropdownOpen(false);
  };

  const handleHomeClick = () => {
    setMenuVisible(true); // Reveal dropdown menu
    navigate("/"); // Go to landing page
  };

  return (
    <nav className="bg-blue-600 text-white px-6 py-4 shadow relative z-50">
      <div className="flex items-center gap-6">
        <button
          onClick={handleHomeClick}
          className="font-semibold hover:underline"
        >
          Home
        </button>

        {/* Show dropdown only if menuVisible is true */}
        {menuVisible && (
          <div className="relative">
            <button
              onClick={() => setDropdownOpen(!dropdownOpen)}
              className="font-semibold hover:underline flex items-center"
            >
              AAMS <span className="ml-1">â–¾</span>
            </button>

            {dropdownOpen && (
              <ul className="absolute mt-2 w-48 bg-white text-black rounded shadow-lg z-50">
                {menuItems.map((item) => (
                  <li
                    key={item.name}
                    className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => handleNavigate(item.path)}
                  >
                    {item.name}
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
